    private void submitActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_submitActionPerformed
                                      
    try {
        if (guard.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Guardian name is required!", "Validation Error", JOptionPane.ERROR_MESSAGE);
            return;
        }
        if (dress.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Address is required!", "Validation Error", JOptionPane.ERROR_MESSAGE);
            return;
        }
        if (num.getText().length() > 15 || !num.getText().startsWith("+63")) { // Adjust length based on your schema
            JOptionPane.showMessageDialog(this, "Contact number is invalid! It must start with '+63' and be within the allowed length.", "Validation Error", JOptionPane.ERROR_MESSAGE);
            return;
        }
        if (!gacc.getText().endsWith("@gmail.com")) {
            JOptionPane.showMessageDialog(this, "Please enter a valid Gmail address ending with '@gmail.com'.", "Validation Error", JOptionPane.ERROR_MESSAGE);
            return;
        }
        if (petsa.getDate() == null) {
            JOptionPane.showMessageDialog(this, "Appointment date is required!", "Validation Error", JOptionPane.ERROR_MESSAGE);
            return;
        }
        if (datesubmit.getDate() == null) {
            JOptionPane.showMessageDialog(this, "Submit date is required!", "Validation Error", JOptionPane.ERROR_MESSAGE);
            return;
        }

        SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd");
        String formattedDate = sdf.format(petsa.getDate());
        SimpleDateFormat sda = new SimpleDateFormat("yyyy-MM-dd");
        String formattedDat = sda.format(datesubmit.getDate());

        String selectedTimeStr = time.getSelectedItem().toString();
        SimpleDateFormat timeParser = new SimpleDateFormat("h:mm a");
        SimpleDateFormat timeFormatter = new SimpleDateFormat("HH:mm:ss");
        String formattedTime = timeFormatter.format(timeParser.parse(selectedTimeStr));

        Connection con = DriverManager.getConnection("jdbc:mysql://localhost/lmrtherapycenter", "root", "");

        String sql = "INSERT INTO appointment (guardianname, address, contact, gmail, appointment_date, appointment_time, submitdate) " +
                     "VALUES (?, ?, ?, ?, ?, ?, ?)";
        PreparedStatement pst = con.prepareStatement(sql);

        pst.setString(1, guard.getText());
        pst.setString(2, dress.getText());
        pst.setString(3, num.getText());
        pst.setString(4, gacc.getText());
        pst.setString(5, formattedDate);
        pst.setString(6, formattedTime);
        pst.setString(7, formattedDat);

        int rowsAffected = pst.executeUpdate();
        if (rowsAffected > 0) {
            JOptionPane.showMessageDialog(this, "Appointment set successfully!");
        } else {
            JOptionPane.showMessageDialog(this, "Failed to set the appointment.");
        }

        guard.setText("");
        dress.setText("");
        num.setText("");
        gacc.setText("");
        petsa.setDate(null);
        time.setSelectedIndex(0);
        datesubmit.setDate(null);

        pst.close();
        con.close();
    } catch (ParseException e) {
        JOptionPane.showMessageDialog(this, "Date/Time Parse Error: " + e.getMessage());
    } catch (Exception e) {
        JOptionPane.showMessageDialog(this, "Unexpected Error: " + e.getMessage());
    }


    }//GEN-LAST:event_submitActionPerformed

