    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        String UserName, Password, query, passdb = null;
        String SUrl, SUser, SPass;
        SUrl = "jdbc:mysql://localhost:3306/lmrtherapycenter";
        SUser = "root";
        SPass = "";
        int notFound = 0;
        try {
            Class.forName("com.mysql.cj.jdbc.Driver");
            Connection con = DriverManager.getConnection(SUrl, SUser, SPass);
            Statement st =con.createStatement();
            if("".equals(user.getText())){
                JOptionPane.showMessageDialog(new JFrame(), "Username is required", "Error",
                        JOptionPane.ERROR_MESSAGE);
            }else if("".equals(pass.getText())){
                JOptionPane.showMessageDialog(new JFrame(), "Password is required", "Error",
                        JOptionPane.ERROR_MESSAGE);
            } else {
                    UserName = user.getText();
                    Password = pass.getText();
                    
                    
                    query = "SELECT * FROM admin WHERE username = '"+UserName+"'";
                    ResultSet rs = st.executeQuery(query);
                    while (rs.next()){
                        passdb = rs.getString ("password");
                        notFound = 1;
                    }
                    if(notFound == 1 && Password.equals(passdb)){
                        Admins ad = new Admins();
                    ad.setVisible(true);
                    this.dispose();
                    }else{
                        JOptionPane.showMessageDialog(new JFrame(), "Incorrect username or password.",
                                "Error", JOptionPane.ERROR_MESSAGE);
                    }
                    pass.setText("");
                    
                    
                    
                }
        }catch(Exception e){
            System.out.println("Error!" + e.getMessage());
        }
    }//GEN-LAST:event_jButton1ActionPerformed

